package models;import com.fasterxml.jackson.annotation.JsonBackReference;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import io.ebean.Ebean;import io.ebean.Model;import io.ebean.annotation.JsonIgnore;import javax.persistence.*;import java.util.ArrayList;import java.util.Arrays;import java.util.List;@Entity@Table(name = "actor")public class ActorModel extends Model {    @Id    @Column(name = "id")    private int id;    @Column(name = "name")    private String name;    @ManyToMany    @JsonIgnoreProperties("actors")    private List<DVDModel> movies;    public ActorModel(String name){        this.name = name;        Ebean.save(this);    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public List<DVDModel> getBooks() {        return movies;    }    public void setMovies(List<DVDModel> dvdModels) {        this.movies = dvdModels;    }    public void addMovie(int dvd){        DVDModel d = Ebean.find(DVDModel.class,dvd);        if(d==null){        }else {        this.movies.add(Ebean.find(DVDModel.class,dvd));        }    }}